const readlineSync = require('readline-sync');
var ConInput = exports;

ConInput.getConsoleInput = function (promptText, acceptBlank) {
	var passedAcceptBlank = acceptBlank;
	var answer;

	while (true) {
		answer = readlineSync.question(promptText);

		if (answer === "\u0003") {
			// Ctl-C
			process.exit();
		} else if (!passedAcceptBlank && (answer === '')) {
			
		} else {
			break;
		}
	}
	return answer;
}

ConInput.getConsoleInput = function (promptText) {
	var passedAcceptBlank = false;
	var answer;

	while (true) {
		answer = readlineSync.question(promptText);

		if (answer === "\u0003") {
			// Ctl-C
			process.exit();
		} else if (!passedAcceptBlank && (answer === '')) {
			
		} else {
			break;
		}
	}
	return answer;
}

ConInput.getConsoleFloatInput = function (promptText) {
	var number = parseFloat(getConsoleInput(promptText));
	while(isNaN(number)){
		number = parseFloat(getConsoleInput(promptText));
	}
	return number;
}

ConInput.getConsoleIntegerInput = function (promptText) {
	var number = parseInt(getConsoleInput(promptText));
	while(isNaN(number)){
		number = parseInt(getConsoleInput(promptText));
	}
	return number;
}
